
def suma(num1: float, num2: float) -> float:
    return num1 + num2

def resta(num1: float, num2: float) -> float:
    return num1 - num2

def producto(num1: float, num2: float) -> float:
    return num1 * num2

def division(num1: float, num2: float) -> float:
    return num1 / num2 if num2 > 0 else 0
